    if(session) {
        const today = new Date()
        const currentDate = 
        today.getFullYear() + '-' + today.getMonth() + '-' + today.getDate()

        const todaysCheckin = await db
        .collection('markers')
        .doc (currentDate)
        .collection('checks')
        .doc(session.user.sub)
        .get()
