            radius: 1000
        })
        .get()
        const checkinsList = []
       checkins.docs.forEach(doc => {
          checkinsList.push({
              id: doc.id,
              status: doc.data().status,
              coords: {
                  lat: doc.data().coordinates.latitude,
                  long: doc.data().coordinates.longitude
              },
              distance: distance(
                  todaysData.coordinates.latitude, 
                  todaysData.coordinates.longitude,
                  doc.data().todaysData.coordinates.latitutude,
                  doc.data().todaysData.coordinates.latitutude
                  ).toFixed(2)
          })
       })
    }
        return {
            props: {
                isAuth: true,
                user: session.user,
                forceCreate: false,
                checkins: checkinsList
            }
        }
    }

    return {
        props: {
            isAuth: false,
            user: {}
        }
    }
}
